INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (51,0,'2010-09-19 09:42:57.0',null,null,null,null,17,null,'维格尚品','fs:/attachment/cms/201009201450162679.jpg','<p>	<strong>产品规格：</strong></p><p>	双人床：1-2005B12-180&nbsp;&nbsp;&nbsp;&nbsp; 1846(1800)*2230(2000)*900<br />	床头柜：1-2005NT6&nbsp;&nbsp;&nbsp;&nbsp; 550*400*430<br />	卧室柜：4-QM052WG6003&nbsp;&nbsp;&nbsp;&nbsp; 2038*600*2190<br />	梳妆台：1-2005T5-2&nbsp;&nbsp;&nbsp;&nbsp; 1000*480*720<br />	梳妆凳：4-2005D11&nbsp;&nbsp;&nbsp;&nbsp; 450*350*430</p><p>	<strong><strong style="font-size: 13px; color: rgb(201, 0, 0);">静谧、温馨、惬意&hellip;&hellip; 随心实现您的梦想卧室！</strong><br />	<strong>多款床品搭配，不同面料选择，全力营造您的个人理想睡眠空间。</strong></strong></p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (52,0,'2010-09-19 09:44:22.0',null,null,null,null,17,null,'经典空间','fs:/attachment/cms/201009201447442392.jpg','<p>	<strong>产品规格：</strong></p><p>	双人床：N05B4-180 &nbsp;&nbsp;&nbsp;&nbsp; 1846*2170*880<br />	衣&nbsp; 柜：N20D05WB44-1 &nbsp; 1630*620*2236<br />	床头柜：N05NT5&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;495*425*550<br />	抽屉柜：N20C05WB22-1&nbsp;&nbsp; 830*620*1148</p><p>	<strong><strong style="font-size: 13px; color: rgb(201, 0, 0);">中式设计 营造了安静、温馨卧室<br />	</strong><strong>胡桃色给人一种沉浸在自然中的稳定感；采用软包的床头设计，在实木家具的质感环境中营造了安静、温馨的味道。 </strong></strong></p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (53,0,'2010-09-19 09:50:01.0',null,null,null,null,17,null,'紫涵韵','fs:/attachment/cms/201009201436043872.jpg','<p>	月圆不了古梦，雪映不出山河，唯有梦中领略盛世唐朝的风采，一如紫菡韵的唐纹设计，</p><div>	是梦、是情感，更是一种向往。盛唐元素的大胆运用，配以香槟色彩，产品更具情调，造型简洁凝练，产品生产工艺创新细腻、线条精美流畅、颜色比例 搭配和谐，似一股清新而又神秘的气息，回荡在现代物欲繁华的都市之中，沉稳中包含着气质，简约中包含着艺术。从而展现出丰富而广阔的生活内涵，更能抒发现 代人的审美情趣。</div>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (54,0,'2010-09-19 10:01:41.0',null,null,null,null,8,null,'品牌理念','','<table border="0" cellpadding="0" cellspacing="0" style="width: 100%;" width="100%">	<tbody>		<tr>			<td colspan="3">				<p align="left">					1987 年，某某家具品牌在充满着对欧洲自由主义生活 态度和西方视觉审美艺术的美好敬意下华丽诞生！在走过的23年岁月里，某某家具以稳健的经营、雄厚的实力、成熟的产品以及星级的服务，已发展成为集设计、 生产、销售于一体的大型、规范化家具集团。现为中国家具协会副理事单位，拥有&quot;中国名牌&quot;、&quot;中国驰名商标&quot;、&quot;中国环境标志（即十环环保认证）&quot;、2009消费者最喜爱的绿色商标&quot;、政府优先采购品牌等多项国家权威荣誉。<br />					&nbsp;&nbsp;&nbsp;&nbsp;某某家具集团有限公司在职员工3,000多名，拥有6个家具生产基地，80万平米的厂区规模，销售网络覆盖全国拥有近600家专卖店，其中面积在5000平米以上的独立专卖店有四十余家。<br />					&nbsp;&nbsp;&nbsp;&nbsp;某某集团制造系统六个大型制造基地，拥有世界先进的板式家具生产线和实木家具生产线，80%的设备均由德国、意大利等地进口。斥资2000万从丹 麦引进国际一流的实木压缩弯曲加工设备和技术，目前，某某集团是中国唯一拥有此项技术和设备的家具企业。</p>			</td>		</tr>		<tr>			<td style="height: 5px;">				&nbsp;</td>			<td style="height: 5px;">				&nbsp;</td>			<td style="height: 5px;">				&nbsp;</td>		</tr>		<tr>			<td style="width: 4%;">				<p align="right">					&nbsp;</p>			</td>			<td style="width: 18%;">				<p align="left">					<strong>某某理念：</strong></p>			</td>			<td style="width: 78%;">				&nbsp;</td>		</tr>		<tr>			<td style="height: 7px;">				&nbsp;</td>			<td style="height: 7px;">				&nbsp;</td>			<td style="height: 7px;">				&nbsp;</td>		</tr>		<tr>			<td colspan="3">				<p align="left">					&nbsp;&nbsp;&nbsp;&nbsp;&ldquo;某某现代家具，欧洲原创设计&rdquo;，某某始终以设计为核心竞争力，并发展成为了中国最有设计的现代家具品牌。<br />					&nbsp;&nbsp;&nbsp;&nbsp;1998年，某某引进中国第一个国际家具设计师，开启中国家具行业自主设计之门，此后，某某一直坚持与世界知名的设计大师合作，并成立某某国际设计联盟，引领中国家具设计走上自主创新之路。&nbsp;<br />					&nbsp;&nbsp;&nbsp;&nbsp;我们期望通过设计开启美好生活，满足您对美好家园的孜孜追求。</p>			</td>		</tr>		<tr>			<td colspan="3" style="height: 8px;">				&nbsp;</td>		</tr>		<tr>			<td style="width: 4%;">				<p align="right">					&nbsp;</p>			</td>			<td style="width: 18%;">				<p align="left">					<strong>我们的责任：</strong></p>			</td>			<td>				&nbsp;</td>		</tr>		<tr>			<td colspan="3" style="height: 8px;">				&nbsp;</td>		</tr>		<tr>			<td colspan="3">				<p align="left">					&nbsp;&nbsp;&nbsp;&nbsp;传播经典家居文化、发展中国家具设计。以国际化、多元化的生活观念和家居用品，创造崭新生活方式；以绿色生态家居环境，提供健康安全的家居用品；以领先环保理念和环保行动，忠实履行对消费者、社会、自然的责任。</p>			</td>		</tr>		<tr>			<td colspan="3" style="height: 8px;">				&nbsp;</td>		</tr>		<tr>			<td style="width: 4%;">				<p align="right">					&nbsp;</p>			</td>			<td style="width: 20%;">				<p align="left">					<strong>我们的销售模式：</strong></p>			</td>			<td>				&nbsp;</td>		</tr>		<tr>			<td colspan="3" style="height: 8px;">				&nbsp;</td>		</tr>		<tr>			<td colspan="3">				<p align="left">					&nbsp;&nbsp;&nbsp;&nbsp;某某开创中国&ldquo;家居独立店&rdquo;的销售模式，通过营造时尚的家居环境为您提供充分的选择空间。目前某某近600家专卖店已遍布中国，以&ldquo;设计&rdquo;和&ldquo;服务&rdquo;结识天南海北的朋友。</p>			</td>		</tr>	</tbody></table>',1,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (55,0,'2010-09-19 10:09:53.0',null,null,null,null,10,null,'联系我们','fs:/attachment/cms/201009191011017554.jpg','	公司地址：北京市某区XXX号<br />	邮政编码：100000<br />	销售热线：010-98765432 87654321<br />	传真号码：010-12345678<br />	联 系 人：刘先生 王先生<br />	公司网址：www.abcde.com<br />	电子邮箱：web@XXX.com',1,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (57,0,'2010-09-19 09:25:36.0',null,null,null,null,11,null,'华东区销售经理','','<div class="jobitem">	职位类型：全职<br />	工作经验：5年以上<br />	学历要求：本科<br />	招聘人数：3名<br />	发布时间：2010-03-13</div><div class="jobtit">	<strong>职位描述</strong></div><div class="jobintro">	本公司即将设立上海办事处，招聘华东区销售经理一名，负责拓展华东地区业务</div><div class="jobtit">	<strong>招聘要求</strong></div><div class="jobintro">	具有良好的销售和团队管理理念三年以上销售工作经验，一年以上销售经理经验工作责任心强，能承受较大的工作压力</div><p>	联 系 人：王先生<br />	联系电话：0571-12344567<br />	电子邮件：job@mail.com</p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (58,0,'2010-09-19 09:26:03.0',null,null,null,null,11,null,'在线客服人员','','<p>	<strong>工作职责：</strong><br />	1、网络在线与客户沟通，对客户的需求提供在线咨询，并实现销售；<br />	2、根据客户订单，处理销售订单系统并为工厂的排产生成订单；<br />	3、家具发货情况的查询及追踪；<br />	4、售后问题的接入及处理。<br />	5、与其他部门协调，做好客户的售前与售后工作。<br />	<strong>工作要求：</strong><br />	1、限女生，专科以上学历，户籍不限，22-28岁；具有零售行业、服务行业、呼叫中心、网络销售客服等相关服务经验者优先。<br />	2、熟练电脑基本操作，要熟悉OFFICE软件；中文输入50字/分钟以上，善于网络交流，沟通能力强。通过在线聊天软件、解答疑问，促成交易；<br />	3、处理问题的应变能力强，并能与客户进行无障碍的电话沟通，以解决客户咨询及售后服务问题。<br />	4、有团队合作精神，勤劳质朴，吃苦耐劳。能承受一定的工作压力。<br />	5、能适应轮班轮休工作制，两班倒，白班：8：30-17：00，晚班：17：00-24：00，每周休息一天，节假日正常休息。提供免费住宿。工作日公司提供午餐（或餐补）。<br />	6、有学习新知识的渴望和优秀的学习能力。<br />	7、良好的团队协作能力、沟通能力与学习能力；</p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (59,0,'2010-09-19 09:26:37.0',null,null,null,null,11,null,'配件采购（暑期实习生）','','<p>	<strong>岗位职责：</strong><br />	1、协助负责零星采购物料的配件采购；<br />	2、将检验合格的物料按流程入库；<br />	3、照公司规定办理款项支付；<br />	4、制定合理的安全库存数；&nbsp;5、配件采购供应商的管理。&nbsp;</p><p>	<strong>任职资格：</strong><br />	1、即将升大三（专科）、大四（本科）的在校学生，实习期为暑假（7、8月）；<br />	2、机电一体化或相关专业。<br />	3、计算机操作熟练。</p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (60,0,'2010-09-19 09:27:02.0',null,null,null,null,11,null,'空间规划设计师','','<p>	<strong>岗位职责：</strong>&nbsp;<br />	1、进行日常方案的空间规划设计及投标标书的制作；&nbsp;<br />	2、对营销的设计支持。&nbsp;<br />	<br />	<strong>任职资格：</strong>&nbsp;<br />	1、室内、装饰设计相关专业毕业，具有专业办公家具的结构及空间布置的常识；&nbsp;<br />	2、有较强的视觉搭配能力，能熟练操作AutoCAD&nbsp;、PowerPoint、Photoshop&nbsp;、3DMAX等软件。&nbsp;<br />	3、具有较强的空间想象能力、文字和口头表达能力；&nbsp;<br />	4、学习能力强、热爱设计工作、有创新精神。</p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (61,100,'2010-09-19 10:11:45.0',null,null,null,null,5,null,'租车手续','','	<strong>&lt;企业单位&gt;</strong><br />	营业执照副本、组织机构代码证及复印件加盖公章、法人代表身份证及复印件。如法人不能在合同上签字，须有法人委托书、经办人身份证和驾驶证、合同章　<br />	<strong>&lt;事业单位&gt;</strong><br />	上级单位介绍信、组织机构代码证及复印件加盖公章，经办人身份证和驾驶证、合同章<br />	<strong>&lt;外国驻京机构&gt;</strong><br />	登记证原件、派出机构证明及担保函、首席代表工作证、身份证原件及复印件、合同章<br />	<strong>&lt;个人&gt;</strong><br />	身份证、北京市城八区全家户口本、驾驶证、租金、押金',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (66,0,'2010-09-20 10:31:42.0',null,null,null,null,8,null,'加盟推广','','<div class="content_xinxi">	<p>		这是一个城市化进程加速的年代，<br />		这是一个消费市场年年攀升的年代，<br />		这是一个家具需求量每年以10%增长的市场，<br />		这是一个诱人的&ldquo;奶酪&rdquo;，更是不可错过的时机。<br />		如果您遇到了好的项目，如果您正准备积聚财富，如果您想发展事业一展宏图，<br />		<span class="red">那么，这个触手可及的机遇向您伸出了橄榄枝：四川﹑湖南﹑贵州﹑江西﹑山东﹑黑龙江﹑辽宁﹑吉林内蒙古&hellip;&hellip;<br />		正在启动加盟合作，期待精彩合作。</span></p></div><div class="content_youshi">	<span style="color: rgb(255, 0, 0);"><span style="font-size: 14px;"><strong>我们的优势：</strong></span></span><br />	<ul>		<li>			23年蓬勃发展，国际化原创设计理念，引领中国家具行业；</li>		<li>			全球7家前沿设计所，41位国际著名设计大师，为曲美支持高标准设计平台；</li>		<li>			亚洲最大的弯曲木制造企业；</li>		<li>			强大的产品供应体系，自有占地超过1000亩的生产基地和国际领先的家具生产线；</li>		<li>			专业培训体系，实战指导，全程保驾护航；</li>		<li>			战略先行营销，精准推广投放，领跑市场前沿；</li>		<li>			完善的售后系统，服务体系科学﹑完善；</li>		<li>			中国驰名品牌﹑中国名牌产品﹑十环认证产品，&ldquo;2009年消费者最喜爱的绿色商标&rdquo;﹑政府优先采购环保产品；</li>		<li>			坦诚合作，优厚政策，长期激励；</li>	</ul></div>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (67,0,'2010-09-20 10:32:27.0',null,null,null,null,8,null,'营销网络','','<p>	<img alt="" src="fs:/attachment/ckeditor/201009202232226251.gif" style="width: 560px; height: 469px;" /></p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (68,0,'2010-09-21 09:46:48.0',null,null,null,null,18,null,'经典小户型装修装修方案','','<div style="text-align: center;">	<div class="article_intro">		核心提示：&nbsp;&nbsp;这是一套58平米的单身公寓，风格以简约素雅，大方实用为主，适合追求简约素雅的年轻白领居住，打开房门，入户后是条长长的过道。过道的尽头有餐桌和以赫本为主题的装饰画。风格简约而干净，非常迎合现在年轻的白领</div></div><div class="text_content">	<div id="MyContent">		<p>			&nbsp;&nbsp;这是一套58平米的单身公寓，风格以简约素雅，大方实用为主，适合追求简约素雅的年轻白领居住，打开房门，入户后是条长长的过道。过道的尽头有餐桌和以赫本为主题的装饰画。风格简约而干净，非常迎合现在年轻的白领追求。</p>		<p>			<a href="http://www.lxzs.net/UploadFiles/2010-09/lxzsnet/2010091210194456111.jpg" target="_blank"><img alt="点击浏览下一页" border="0" src="http://www.lxzs.net/UploadFiles/2010-09/lxzsnet/2010091210194456111.jpg" width="400" /></a></p>		<p>			进门的小走廊以及从玄关看客厅：</p>		<p>			&nbsp;<a href="http://www.lxzs.net/UploadFiles/2010-09/lxzsnet/2010091210264193659.jpg" target="_blank"><img alt="点击浏览下一页" border="0" src="http://www.lxzs.net/UploadFiles/2010-09/lxzsnet/2010091210264193659.jpg" width="400" /></a></p>		<p>			&nbsp; 客厅是整个公寓的核心，不规则房间形状和开放的布局，便于你和朋友在此闲聊。两扇大窗户提供了充足的阳光和空气。沙发、电视、餐桌，两侧连接了厨房和卧室。</p>		<p>			进门右手侧厨房：</p>		<p>			<a href="http://www.lxzs.net/UploadFiles/2010-09/lxzsnet/2010091210293511056.jpg" target="_blank"><img alt="点击浏览下一页" border="0" src="http://www.lxzs.net/UploadFiles/2010-09/lxzsnet/2010091210293511056.jpg" width="400" /></a></p>		<p>			&nbsp; 卧室在房间的尽头，包括一张大床和大衣柜。占了一面墙的大衣柜保证了充足的储物空间。床头的墙壁被漆成白色碎花图案。</p>		<p>			<br />			<a href="http://www.lxzs.net/UploadFiles/2010-09/lxzsnet/2010091210335968845.jpg" target="_blank"><img alt="点击浏览下一页" border="0" src="http://www.lxzs.net/UploadFiles/2010-09/lxzsnet/2010091210335968845.jpg" width="400" /></a></p>		<p>			这样装修出来的房子简约、素雅、干净，非常适合年轻的白领。</p>	</div></div>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (69,0,'2010-09-21 09:48:25.0',null,null,null,null,18,null,'三分硬装七分软打造完美居家','','<div >	<div class="article_intro">		核心提示：&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;目前很多业主在装修房子的时候，希望把房子装修得更加豪华，档次提高一些，以防落伍，如增加了很多特别的造型和结构，像罗马柱、圆拱门、墙体浮雕等等，增加了很多硬装修的东西，当然装修出来的房子也是别有</div></div><div class="text_content">	<div id="MyContent">		<p>			&nbsp;&nbsp;&nbsp; 目前很多业主在装修房子的时候，希望把房子装修得更加豪华，档次提高一些，以防落伍，如增加了很多特别的造型和结构，像罗马柱、圆拱门、墙体浮雕等等，增 加了很多硬装修的东西，当然装修出来的房子也是别有一番独特风味。不过随着装修潮流不断推进，装修造型的不断翻新，今天流行的造型，三年后再看不少业主难 免会咂舌，后悔当初应该少点硬装多点软装。一般来说，完美的装修过程是三分硬装修，七分软装饰。</p>		<p>			<br />			&nbsp;&nbsp; &nbsp;硬装修的目的是让居室功能实现最大化，满足居住者的使用需求。而软装饰则可以提升整个居室的档次与品位。从费用上算，硬装修要增加造型结构动不动就是几万元，而软装饰如布艺、小装饰摆设只需几百、几千元就能达到扮靓家居的目的了。</p>		<p>			<br />			&nbsp; &nbsp; 在装修时，业主应该尽量简化硬装修，简单、素雅的硬装可以为后期换装预留充分的空间。与硬装修相比，软装饰改动就简单得多了。软装饰最大的特点就是&ldquo;不喜 欢就换&rdquo;，居住者不仅可以根据时尚流行趁势配搭家具、窗帘、布艺及各类家居饰品，通过软装饰来体现居住者的个性品位，还可以根据心情、季节进行换装，随时 让家居保持新鲜感。</p>	</div></div>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (70,0,'2010-09-21 09:53:10.0',null,null,null,null,19,null,'总经理办公室的风水布置一般原则','','<p>	<font size="2"><span lang="EN-US">1.</span>门<span lang="EN-US">:</span>进则通<span lang="EN-US">,</span>出则畅。</font><br />	<span lang="EN-US"><font size="2">2.</font></span><font size="2">椅<span lang="EN-US">:</span>坐则稳<span lang="EN-US">,</span>立则直<span lang="EN-US">(</span>忌头悬梁<span lang="EN-US">,</span>头上有吊灯<span lang="EN-US">),</span>面则润<span lang="EN-US">(</span>对面设鱼缸或水盆景<span lang="EN-US">),</span>背则耸<span lang="EN-US">(</span>座背有靠山</font><font size="2"><span lang="EN-US">),<br />	3.</span>整体效果<span lang="EN-US">:</span>阴阳和谐<span lang="EN-US">,</span>动静协调<span lang="EN-US">,</span>迎送有位<span lang="EN-US">,</span>主宾有序<span lang="EN-US">,</span>定变有常<span lang="EN-US">,</span>十方有分。</font><br />	<span lang="EN-US"><font size="2">4.</font></span><font size="2">方和位的确定<span lang="EN-US">: </span>十方八向构成了<span lang="EN-US">&ldquo;</span>方向<span lang="EN-US">&rdquo;</span>的内涵<span lang="EN-US">,</span>人在<span lang="EN-US">&ldquo;</span>向<span lang="EN-US">&rdquo;</span>中为客<span lang="EN-US">,</span>人在<span lang="EN-US">&ldquo;</span>方<span lang="EN-US">&rdquo;</span>中为主。老板办公室布置以<span lang="EN-US">&ldquo;</span>方<span lang="EN-US">&rdquo;</span>为主<span lang="EN-US">,&ldquo;</span>方<span lang="EN-US">&rdquo;</span>是指前、后、左、右、右前、左后、左前、右后、上、下十个方位构成<span lang="EN-US">,&ldquo;</span>向<span lang="EN-US">&rdquo;</span>是由指南针所指示定下来的向位<span lang="EN-US">,</span>特指东、西、南、北、东北、西北、东南、西南八个向位<span lang="EN-US">.</span></font></p><p>	<strong><span style="font-family: 宋体;"><font size="2">总经理、总裁写字台的布置<span lang="EN-US">:</span></font></span></strong></p><p>	<font size="2"><span lang="EN-US">1.</span>写字台左前方应通畅无阻<span lang="EN-US">,</span>进退有径<span lang="EN-US">,(</span>左手对着门<span lang="EN-US">),</span>此方为青龙位<span lang="EN-US">,</span>青龙喜游、易动、蹿跃无阻则生财气<span lang="EN-US">,</span>上下翻飞无碍则生财喜之气。<span lang="EN-US">(</span>可放置饮水机</font><font size="2"><span lang="EN-US">)</span></font></p><p>	<font size="2"><span lang="EN-US">2.</span>写字台的正前方应阔绰明亮<span lang="EN-US">,</span>宜放矮椅短凳<span lang="EN-US">,</span>此方为朱雀位<span lang="EN-US">,</span>朱雀喜鸣易飞<span lang="EN-US">,</span>故宜于面对面交流。此处应洁净<span lang="EN-US">,</span>忌摆放污物<span lang="EN-US">,</span>宜对明窗<span lang="EN-US">,</span>若对面是墙则可挂地图或名人字画及催人奋进的条幅、警言、名句。写字台的正前方对面墙设鱼缸或水盆景.</font></p><p>	<font size="2"><span lang="EN-US">3.</span>写字台的台面正前置放砚台<span lang="EN-US">,</span>笔筒。</font></p><p>	<span lang="EN-US"><font size="2">4.</font></span><font size="2">写字台右前方置物要略高于左前方置物。右前方为白虎方<span lang="EN-US">,</span>白虎喜山<span lang="EN-US">,</span>易燥<span lang="EN-US">,</span>宜摆放个人喜爱的固体装饰品<span lang="EN-US">,</span>最好是玉石<span lang="EN-US">,</span>玉石代表山<span lang="EN-US">,</span>象征白虎卧山川<span lang="EN-US">,</span>忌放鱼缸<span lang="EN-US">,</span>饮水机以及与水有关的盆景。</font></p><p>	<span lang="EN-US"><font size="2">5.</font></span><font size="2">老板座椅背后不能留出他人进出之路<span lang="EN-US">,</span>宜窄不宜宽<span lang="EN-US">,</span>否则位置坐不稳。</font></p><p>	<span lang="EN-US"><font size="2">6.</font></span><font size="2">老板座椅背后宜放书柜和饰品柜<span lang="EN-US">,</span>忌放展品柜<span lang="EN-US">,</span>否则<span lang="EN-US">,</span>让人动来摸去也犯了大忌。</font></p><p>	<span lang="EN-US"><font size="2">7.</font></span><font size="2">老板座椅背正后方为玄武<span lang="EN-US">,</span>玄武<span lang="EN-US">(</span>龟蛇<span lang="EN-US">)</span>喜暗易睡<span lang="EN-US">,</span>引伸背有靠<span lang="EN-US">,</span>稳稳实实。<span lang="EN-US">(</span>可置半环形有机玻璃<span lang="EN-US">,</span>有靠山<span lang="EN-US">.</span>有印罩</font><font size="2"><span lang="EN-US">)</span></font></p><p>	<font size="2"><span lang="EN-US">8.</span>老板办公桌的左后方为腾蛇位<span lang="EN-US">,</span>腾蛇喜藏于物下<span lang="EN-US">,</span>易昼伏夜出<span lang="EN-US">,</span>宜摆放花木或矮桌、茶几、沙发<span lang="EN-US">,</span>招待客人之用。昼动夜静<span lang="EN-US">,</span>给下属及子孙平安康泰。</font></p><p>	<span lang="EN-US"><font size="2">9.</font></span><font size="2">老板办公桌右后方为勾陈位<span lang="EN-US">,</span>勾陈喜蜇伏于重物下<span lang="EN-US">,</span>易固守持恒<span lang="EN-US">,</span>故此处应摆设重物<span lang="EN-US">,</span>宜放保险柜、金属类器物、镇物。</font></p><p>	<span lang="EN-US"><font size="2">10.</font></span><font size="2">老板写字台左为角旗<span lang="EN-US">,</span>右为印鼓。角旗喜张扬易发号<span lang="EN-US">,</span>应五彩缤纷<span lang="EN-US">,</span>宜在墙面上张挂荣誉证书<span lang="EN-US">,</span>名誉牌匾、奖状等<span lang="EN-US">,</span>以宣传自己或自己崇拜的偶像。旗张角响则响应者众<span lang="EN-US">,</span>忠诚之士勇于向前。右为印鼓<span lang="EN-US">,</span>印鼓喜近主<span lang="EN-US">,</span>易施令<span lang="EN-US">,</span>故在写字台的右手位上宜摆放电脑、签字笔、名片、印台<span lang="EN-US">,</span>公司<span lang="EN-US">CI</span>标识物等等。鼓位为整个房间的最高地方<span lang="EN-US">,</span>鼓鸣印彰则令行禁止<span lang="EN-US">,</span>士属无不遵纪守法<span lang="EN-US">,</span>秋毫无犯。</font></p><p>	<span lang="EN-US"><font size="2">11.</font></span><font size="2">把电脑设在鼓位<span lang="EN-US">(</span>右手位<span lang="EN-US">)</span>。</font></p><p>	<span lang="EN-US"><font size="2">12.</font></span><font size="2">标记<span lang="EN-US">:</span>双<span lang="EN-US">M</span>代表<span lang="EN-US">&rdquo;</span>门<span lang="EN-US">&rdquo;,</span>三合体代表<span lang="EN-US">&rdquo;</span>饮水机<span lang="EN-US">&rdquo;.</span>颜色<span lang="EN-US">:</span>青绿<span lang="EN-US">,</span>绛红<span lang="EN-US">,</span>土黄<span lang="EN-US">.</span>忌金银两色。</font></p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (71,0,'2010-09-21 09:54:21.0',null,null,null,null,19,null,'办公室的色彩设计可以缓解视觉疲劳提高工作效率','','<p>	办公空间的色彩环境设计必须依照该空间的工作性质来考虑。在办公空间中，如果色彩能够起到很好的调节作用，便可以增强员工的工作欲望，提高工作效率，并可以缓解员工的疲劳。</p><p>	&nbsp;&nbsp;&nbsp; 在办公空间中，我们经常看到的一个颜色就是墙壁上那不加修饰的白色，其实白色非常不适合用来装饰办公空间。因为白色的明度最高．光线的反射过于强烈，会在 很大的程度上刺激眼睛．造成视觉疲劳，甚至容易引起头痛，从而降低工作效率。还有少部分办公空间采用过于暗淡的基调色彩．会使久置身其中的员工产生忧郁的 感觉．使工作变得马虎。在办公空间中，除了顶棚使用了白色，其他部分均采用了色彩装饰．墙壁和地面运用了温雅的淡褐色系．视觉不易产生疲劳．在一侧的墙壁 上使用了亮蓝色，蓝色从来都是知性的色彩，让人善于思考，在整体的空间中又活跃了气氛。办公空间以米白色的墙壁和顶棚，灰橄榄色的地毯为整个区域的背景 色，可以有效地舒缓工作的疲劳感。</p><p>	&nbsp;&nbsp;&nbsp; 办公空间是提供给不同类型人的一个办公环境，处在该环境中的人们有着各种性格和感情，因此办公空间不能像私人住宅那样强调个人的色彩喜好。</p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (72,0,'2010-09-21 09:59:46.0',null,null,null,null,20,null,'KTV设计装修之包厢舞池设计','','<p>	作为一种公共建筑形态，KTV不再是简单的一个建筑物了，一边它会渗透着社会文化对建筑及设计的影响，另一边它将多元化的元素表现在自身的空间里面。我们所指的KTV设计是现代社会中非常重要的公共场合。不但，很多时候他们代表的是这个城市文化和品位的主要趋向。&nbsp;<br />	&nbsp;&nbsp;&nbsp;&nbsp;为了提高<strong>KTV设计装修</strong>包厢的娱乐效果，很多KTV包厢设立了华丽的小型舞池，立体豪华的灯光设备，并以电脑自动操控，为聚餐团体提供了新的娱乐项目。KTV舞池设计，都集中在包厢内靠近荧屏处或者中间，一般不设在进门处，既方便顾客边用餐饮边娱乐，又不影响服务员的服务工作。</p><p>	&nbsp;&nbsp;&nbsp; 1、舞池设计<br />	&nbsp;&nbsp;&nbsp;&nbsp;KTV包厢舞池的设计要能增强娱乐效果，制造气氛，又要能吸引客人，同时舞池设计也应当遵循既方便客人娱乐又能让客人饮食的原则。舞池设计要与KTV设计装修包厢的大小及接待人数的能力一致，一般小型KTV包厢，如容纳2&mdash;4人的包厢，舞池一般为1或1．5平方米的方形台面或池面即可，能容纳10人以上的大型KTV包厢的舞池就应大些。因KTV的空间有限，舞池设计采用两种方法：<br />	&nbsp;&nbsp;&nbsp;&nbsp; (1)概念性舞池。即在地面装修时，采用特定的方法制成概念性方形和圆形舞池，如用特殊的色彩或在地板下面安装可变化的彩灯。概念性舞池的设计是一个平面，不妨碍房间作其他用途。<br />	&nbsp;&nbsp;&nbsp; (2)采用特殊的材料，设计成专用的舞池，舞池或高于或低于房间平面，地面通常采用铜地板或玻璃地板。<br />	&nbsp;&nbsp;&nbsp;&nbsp;总之，KTV包厢设计中舞池的设计要达到顾客能相互直接交流，并创造共同娱乐气氛的目的。<br />	&nbsp;&nbsp;&nbsp;&nbsp;KTV包厢中设置的舞池除要符合一般舞池设计的要求外，还要求面积应与KTV包厢大小相协调，同时要求通风良好，卫生设备要符合卫生部门规定的标准。安全设备的设置也是很重要的，安全门的标志灯应清晰可见，并备有紧急照明设备。</p><p>	2、舞池灯光效果<br />	&nbsp;&nbsp;&nbsp;&nbsp;灯光设计作为舞池设计的一部分，用来描绘、渲染舞台，主要运用灯光的明暗、色彩或光线的分布，创造出种种组合光线，增强舞池的效果。<br />	&nbsp;&nbsp;&nbsp; KTV设计包厢舞池灯光首先要考虑的是KTV内的基本照明，同时应充分考虑KTv特有的气氛，以期达到最理想的效果。KTV舞池可选用的灯光种类很多，一般常用的有以下几种：<br />	&nbsp;&nbsp;&nbsp;&nbsp;(1) 光束灯<br />	&nbsp;&nbsp;&nbsp;&nbsp;这是一种较新型的照明灯具，特点是体积小，光束强，聚光效果好。使用时可依需要调配好色彩，透过调光台的控制不断变化明灭，达到场地光色对比鲜明的强烈效果。<br />	&nbsp;&nbsp;&nbsp; (2) 单飞碟转灯<br />	&nbsp;&nbsp;&nbsp;&nbsp; 这种灯具采用不同的方位多电机控制和玻璃涂色胶工艺，其品质和效果均极好。<br />	&nbsp;&nbsp;&nbsp; (3) 扫描灯<br />	&nbsp;&nbsp;&nbsp;&nbsp; 扫描灯分单头、多头等几种，利用强烈的彩色光束轮番扫描全场，造成一种激动而迷幻的感觉。<br />	&nbsp;&nbsp;&nbsp; (4) 宇宙旋转灯<br />	&nbsp;&nbsp;&nbsp;&nbsp; 宇宙旋转灯有圆形、多棱形、橄榄形等多种类型，这种灯具利用电机自动控制，将彩色的光点撒向整个包厢，极为绚丽多彩。<br />	&nbsp;&nbsp;&nbsp; (5) 声控条状满天星<br />	&nbsp;&nbsp;&nbsp;&nbsp;这种灯具设计新颖，机械结构合理，声控灵敏，灯光可跟着音乐节奏变化、闪烁、转动。若以玻璃彩色胶代替色纸，可使灯具具备色彩鲜艳、透明度好、耐高温、不易老化、成本低等优点。<br />	&nbsp;&nbsp;&nbsp; (6) 彩色转盘灯<br />	&nbsp;&nbsp;&nbsp;&nbsp;即在2000瓦聚光灯前面装上可逆马达带动的转盘，转盘上分别蒙上多种色彩的灯光色纸。使用这种灯具时，场地中光色会不停变化，对渲染舞池气氛有很好的作用。<br />	&nbsp;&nbsp;&nbsp;&nbsp;另外，还可将声控彩色灯具装饰在KTV内天花板、窗沿等处，以增添舞池气氛。或在舞池的地面装上各种图案的有机玻璃地板，地板下面装上声控彩灯，随着音乐节奏的变化闪烁，效果很好。若空间可配合，还可装上霓虹灯、镭射彩灯等。<br />	&nbsp;&nbsp;&nbsp; 总之，KTV设计舞池灯光风格各异，应根据KTV空间大小及功能需要来选择，可典雅静谧，可华丽热烈。</p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (73,0,'2010-09-21 10:02:34.0',null,null,null,null,20,null,'不同类型的KTV设计说明','','<p>	KTV包厢是为了满足顾客团体的需要，提供相对独立、无拘无束、畅饮畅叙的环境。相对封闭KTV包厢的布置，应为客人提供一个以围为主，围中有透的空间，KTV包厢的空间是以KTV经营内容为基础。围绕这些问题专家给与以下关于KTV设计的说明：<br />	<br />	&nbsp; 1、根据经营内容和设施确定KTV空间<br />	&nbsp;&nbsp;&nbsp; (1) 酒吧式KTV的空间确定<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 酒吧KTV包厢在提供视听娱乐的同时，还要向顾客提供鸡尾酒等各类饮料，其空间的确定应考虑以下几个方面：<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ①放置电视、点歌器、麦克风等视听设备的空间。一般来说电视音响设备大，占的空间就大。<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ②顾客座位数。接待顾客人数多，沙发所占空间就大，一般酒吧、餐厅将KTV分成大、中、小三种包厢。<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ③摆放饮料的茶几或方型小餐桌。<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ④若KTV包厢内设有舞池，还应提供舞台和灯光空间。<br />	&nbsp;&nbsp;&nbsp; 此外，还应考虑客人座位与电视荧幕的最短距离，一般最小不得小于3&mdash;4米。总之，酒吧KTV的空间应具有封闭、隐密、温馨的特征。酒吧里设有小型两人KTV或四人KTV，以及能容纳十多人的大型KTV。<br />	<br />	<br />	(2) 餐厅式KTV的空间确定<br />	&nbsp;&nbsp;&nbsp; 餐厅式KTV包厢以提供餐饮为主，卡拉OK等娱乐项目为辅，就包厢的空间而言，应根据以下内容来确定：<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ①餐桌大小或餐桌数量。餐桌大小或餐桌数量直接影响餐厅式KTV包厢的面积要求。如提供10人用餐餐桌的占地面积，要小于16人用餐餐桌的占地面积，或两张餐桌所占空间要比一张餐桌所占空间大。<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ②要考虑视听灯光设施或舞台设计效果。<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ③衣帽架。<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ④备餐柜(服务柜)。<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ⑤个别包厢还应专门设洗手间等。<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 餐厅式KTV的空间是为满足用餐需要，应体现宽敞洁净、明亮、舒适的特点。除可供人们娱乐休息之用，还应能缓解人们的紧张情绪，提供客人在轻松愉快的环境中边用餐边交流的氛围。<br />	&nbsp; (3)休闲式KTV的空间确定<br />	&nbsp;&nbsp;&nbsp; 休闲式KTV除必须具备酒吧式KTV的设施外，还要考虑休闲娱乐设施的内容，这类KTV一般占用较大房间或者是套房。因休闲项目不同，其设计也不相同，在KTV空间设计时应充分考虑。</p><p align="center">	休闲式<strong><font color="#0099ff">KTV装修效果图</font></strong> </p><p align="left">	<br />	2、根据接待人数确定KTV包厢空间<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 无论是酒吧、歌舞厅、还是餐厅的KTV包厢，在确定空间时都可根据接待人数，将空间面积分为小型、中型、大型KTV包厢。KTV包厢大小不能说明其豪华程度，一般只反映接待顾客的能力。<br />	(1) 小型KTV包厢<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 酒吧、歌舞厅的小型KTV包厢面积一般在9平方米左右，能接待6人以下的团体顾客。小型KTV包厢<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 配备的设施与大、中型KTV包厢并无两样，只是电视、音响与空间协调时要小一些。从目前酒吧、歌舞厅的经营情况来看，小型KTV包厢占的比重大，这类包厢 的出租率最高，尤其是附设洗手间、吧台、舞池、电话的豪华小型KTV包厢，特别受顾客喜爱。小型KTV包厢要表现出紧密温馨的环境。<br />	&nbsp; (2) 中型KTV包厢<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 面积在11&mdash;15平方米，能接待8&mdash;12人左右，除配备基本的电视、电脑点歌、沙发、茶几、电话等设施外，还应根据实际情况配备吧台、洗手间、舞池等。中型KTV包厢要表现舒适。<br />	&nbsp; (3)大型KTV包厢<br />	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 面积一般在25平方米左右，能同时接待20人的大型KTV包厢在酒吧、歌舞厅中所占的比重较小，一般只有一、二个，设施、功能都比较齐全，表现出豪华宽敞的特点。</p><p align="center">	大型<strong>KTV装修效果图</strong>&nbsp; <strong></p><p align="left">	3、KTV包厢的大、中、小分类<br />	&nbsp;&nbsp;&nbsp; 一般是按能摆放桌的数量分类，小型餐厅KTV包厢一般摆放一个餐桌，中型摆两桌，大型一般为三桌。</p><p align="left">',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (74,0,'2010-09-21 10:06:59.0',null,null,null,null,21,null,'服装店橱窗设计方案','','<div>	<div>		核心提示：橱 窗，是服装店装修设计外观的一个重要组成部分，它有两个作用：一是让顾客辨认商店及其商品；二是吸引顾客进入商店。橱窗通过陈列各种样品，能为商店创造不 同的形象特征。通过显示时尚商品和季节商品，商店能表明自己是不落伍的；通过摆放处理商品，商店能招揽价格敏感型顾客；通过进行艺术化处理，服装店装修就 能吸引行人注意，并显示高雅的格调；通过提供公共服务信息，商店能显示其对社会的关心，从而获得好感。</div></div><p>	橱窗，是服装店装修设计外观的一个重要组成部分，它有两个作用：一是让顾客辨认商店及其商品；二是吸引顾客进入商店。橱窗通过陈列各种样品，能为商店创造不同的形象特征。通过显示时尚商品和季节商品，商店能表明自己是不落伍的；通过摆放处理商品，商店能招揽价格敏感型顾客；通过进行艺术化处理，服装店装修就能吸引行人注意，并显示高雅的格调；通过提供公共服务信息，商店能显示其对社会的关心，从而获得好感。</p><p>	&nbsp;&nbsp;&nbsp; 橱窗尺寸由于商店类型、门面长度不同而有所区别。但长度和宽度的比例一定要符合视觉习惯。一般高、宽的比例以l：1．62为佳，这便是常说的&ldquo;橱窗的黄金定率&rdquo;。</p><p>	&nbsp;&nbsp;&nbsp; 一个构思新颖、主题鲜明、风格独特、手法脱俗、装饰美观、色调和谐的服装店橱窗设计，与整个商店建筑结构和内外环境构成的立体画面，能起美化商店和市容的作用。一般来讲，服装店装修设计应注意以下方面：</p><p>	&nbsp;&nbsp;&nbsp; (1)橱窗横向中心线最好能与顾客的视平线相等，那么，整个橱窗内所陈列的商品都在顾客视野中。</p><p>	&nbsp;&nbsp;&nbsp; (2)在橱窗设计中，必须考虑防尘，防热、防淋、防晒、防风、防盗等，要采取相关的措施。</p><p>	&nbsp;&nbsp;&nbsp; (3)服装店装修设计不能影响店面外观造型，橱窗规模应与商店整体规模相适应。</p><p>	&nbsp;&nbsp;&nbsp; (4)橱窗陈列的商品必须是本商店出售的，而且是最畅销的商品。</p><p>	&nbsp;&nbsp;&nbsp; (5)橱窗陈列季节性商品必须仕字节到来之前一个月预先陈列出来向顾客介绍，这样才能起到迎季宣传的作用。</p><p>	&nbsp;&nbsp;&nbsp; (6)陈列商品时，应先确定主题，无论是多种多类或是同种不同类的商品，均应系统地分种分类依主题陈列，使人一目了然的看到所宣传介绍的商品内容。千万不可乱堆乱摆分散消费者视线。</p><p>	&nbsp;&nbsp;&nbsp; (7) 橱窗布置应尽量少用商品作衬托、装潢或铺底，除根据橱窗面积注意色彩调和、高低疏密均匀外，商品数量不宜过多或过少。服装店装修设计要做到使顾客从远处近处、正面侧面都能看到商品全貌。富有经营特色的商品应陈列在最引人注目的橱窗里。</p><p>	&nbsp;&nbsp;&nbsp; (8)容易液化变质的商品如食品糖果之类，以及日光照射下容易损坏的商品，最好用其模型代替或加以适当的包装。</p><p>	&nbsp;&nbsp;&nbsp; (9)橱窗应经常打扫，保持清洁，特别是食品橱窗。郑州服装店装修公司提醒肮脏的橱窗玻璃，橱窗里面布满灰尘，会给顾客不好的印象，引起对商品的怀疑或反感而失去购买的兴趣。</p><p>	&nbsp;&nbsp; (10)橱窗陈列需勤加更换，尤其是有时间性的宣传以及陈列容易变质的商品尤应特别注意。服装店装修橱窗作更换或布置，必须在当天内完成。</p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (75,0,'2010-09-21 10:08:35.0',null,null,null,null,21,null,'美容院色彩环境设计','','<div>	<div>		核心提示：。 因为在这里有很多的幔帐和床单．如果能够据季节改变它们的色彩，就可以改变空间的感觉．如，春季时采用柔和的鹅黄或者果绿色．可以体现生机勃勃的气象；在 夏季时采用淡淡的湖蓝色．就可以打造清凉的感觉&hellip;&hellip;.不同的色彩可以让空间焕然一新，更带给长期的顾客的一种新鲜、新奇的感觉。这也是利用环境留住顾客的 一种方法。还有美容院内还可以用淡灰紫色的墙壁、洁白的床单打造一个安静、整洁的美容室。这是大多数郑州美容院装修公司会采用的一种方法，当然在美容院中 也很容易见到。</div></div><p>	&nbsp;&nbsp;&nbsp; 进入美容院接受服务的顾客基本上都是女士。所以，设计人员建议美容院的室内色彩环境宜以温馨、洁净为主。而女人与花也有着不解之缘，图509是某美容门诊的接待区，乳白色和浅褐色调的搭配．尽显典雅大方。图510则 是一家美容院的楼梯处装饰，钢管呈瀑布状，从二层的顶棚一直垂到一层地面，上面装饰着紫色的玫瑰花，这样的美容院的装修设计环境是可以使人们心情放松的空 间，应当体现幽静的感觉，幽静则安神，安神则养颜。大部分美容院的室内空间可以分为两个部分：一是接待区，二是美容操作区。</p><p>	&nbsp; &nbsp;接待区往往可以显示一家美容院的整体格调，这个空间的色彩设计也尤为重要。在一个决定顾客消费的空间里．太冷的色调就可能影响到顾客的消费。曾经看到北<a href="http://www.lxzs.net/html/zxzn/sykj/mrysj/2564_2.html"><img alt="点击浏览下一页" border="0" src="http://www.lxzs.net/UploadFiles/2010-06/lxzsnet/image1.png" width="400" /></a>图509<a href="http://www.lxzs.net/html/zxzn/sykj/mrysj/2564_2.html"><img alt="点击浏览下一页" border="0" src="http://www.lxzs.net/UploadFiles/2010-06/lxzsnet/image2.png" /></a><a href="http://www.lxzs.net/html/zxzn/sykj/mrysj/2564_2.html"><img alt="点击浏览下一页" border="0" src="http://www.lxzs.net/UploadFiles/2010-06/lxzsnet/image4.png" /></a><br />	<a href="http://www.lxzs.net/html/zxzn/sykj/mrysj/2564_2.html"><img alt="点击浏览下一页" border="0" src="http://www.lxzs.net/UploadFiles/2010-06/lxzsnet/image3.png" width="400" /></a><br />	&nbsp;</p><p>	京一家美容院的接待区就是采用传统的白色作为顶棚和墙壁的基调色彩，而展示柜和接待台都采用了冷冷的亮蓝色，这样强烈的对比和冷色的运用使整个接待区缺少了温馨的感觉。所以在美容院的接待区中最好采用比较有女人味的色彩组合，建议可以选择柔和、透明、轻盈、宁静的色彩组合，而且要避免太过于强烈的对比。图511是为客人咨询的地方，地面是布满紫红色花瓣的灯箱地台，浅青绿色的墙壁，淡紫色的座椅都体现了一个柔静所在。图512是通过各美容室的走廊，米色、白色的运用使空间幽静的同时也让狭小的通道看起来宽敞一些，而那些岩饰的花朵更活跃了整体的氛围。</p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (76,0,'2010-09-21 10:33:15.0',null,null,null,null,14,null,'服务热线','','<p>	24小时服务热线：010-12345678</p>',1,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (77,0,'2010-09-30 03:53:49.0',null,null,null,null,11,null,'环艺及商用家具设计','','<p>	<strong>岗位职责&nbsp;</strong><br />	1、根据项目要求负责项目中展览、店面、产品造型等有关美术方面的设计与制作；<br />	2、配合项目经理制定工作进度。&nbsp;<br />	<strong>任职资格&nbsp;</strong><br />	1、美术、平面设计、广告等相关专业，大学专科及以上学历；<br />	2、两年以上相关工作经验，手绘能力强者优先；&nbsp;3、具有扎实的美术基础、色彩控制能力强、想象力丰富；<br />	4、积极肯干，吃苦耐劳；具有创新精神及团队协作精神；<br />	5、责任心强，有团队合作精神，有良好的沟通能力；<br />	6、富有创意及执行力，有责任感、表达能力强。&nbsp;&nbsp;&nbsp;公司免费提供一日三餐及住宿</p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
INSERT INTO es_car_article_<userid>_<siteid> (id,sort,add_time,hit,able_time,state,user_id,cat_id,is_commend,title,img,content,sys_lock,lastmodified,page_title,page_keywords,page_description,site_code,siteidlist) VALUES (78,0,'2010-09-30 03:55:54.0',null,null,null,null,11,null,'办公室文员','','<p>	<span><strong>薪资</strong>：1200元+补助+奖金<br />	<strong>要求</strong>：两年以上工作经验，年龄26岁以上</span></p>',0,'2010-09-16 07:36:37.0','','','',100000,null);
