update eop_site set sitename='company新建站点',logofile='fs:/images/default/logo.gif',icofile='fs:/images/default/favicon.ico',keywords='',descript='' where userid=<userid> and id=<siteid>;
CREATE TABLE `es_car_article_<userid>_<siteid>` (
  `id` mediumint(8) NOT NULL auto_increment,
  `sort` smallint(1) default '0',
  `add_time` datetime default NULL,
  `hit` bigint(20) default NULL,
  `able_time` bigint(20) default NULL,
  `state` mediumint(8) default NULL,
  `user_id` bigint(20) default NULL,
  `cat_id` mediumint(8) default NULL,
  `is_commend` mediumint(4) default NULL,
  `title` varchar(255) default NULL,
  `img` varchar(255) default NULL,
  `content` text,
  `sys_lock` mediumint(4) default '0',
  `lastmodified` datetime default NULL,
  `page_title` varchar(255) default NULL,
  `page_keywords` varchar(255) default NULL,
  `page_description` text,
  `site_code` int(11) default '100000',
  `siteidlist` varchar(255) default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;
